import "./FilesTable.css";
import { niceBytes } from "./SizeCalculatorUtil.ts"; 

function FilesTable() {
  let testFiles = [
    {
      id: "1",
      name: "src",
      type: "folder",
      size: 1024,
      updated_at: "2019-06-11 21:24:00",

    },
    {
      id: "2",
      name: "tests",
      type: "folder",
      size: 2048,
      updated_at: "2019-07-11 21:24:00",
    },
    {
      id: "3",
      name: "README",
      type: "file",
      size: 4096,
      updated_at: "2019-07-18 21:24:00",
    },
  ];

  return (
    <table className="list-group">
      <thead className="table-light">
        <tr>
          <th scope="col">Name</th>
          <th scope="col">Date Modified</th>
          <th scope="col">Size</th>
          <th scope="col">...</th>
        </tr>
      </thead>
      <tbody>
        {testFiles.map((file) => (
          <FileListItem file={file} />
        ))}
      </tbody>
    </table>
  );
}

function FileListItem({ file }) {
  return (
    <tr id={file.id} className={"file-list-item"}>
      <FileName file={file} />
      <Time timestamp={file.updated_at} />
      <Size size={file.size}/>
    </tr>
  );
}

const FileName = ({ file }) => (
  <>
    <FileIcon file={file} />
    <td className={"file-name"}> {file.name}</td>
  </>
);

function FileIcon({ file }) {
  let icon = "fa-file-text-o";

  if (file.type === "folder") {
    icon = "fa-folder";
  }

  return (
    <td className={"file-icon"}>
      <i className={`fa ${icon}`} />
    </td>
  );
}

const Time = ({ timestamp }) => {
  return <td className={"time"}>{timestamp}</td>;
};

const Size = ({ size }) => {
  return <td className={"size"}>{niceBytes(size)}</td>;
};


export default FilesTable;
